package com;

public class increament {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
        int b = 2;
        int c;
        int d;
        c = ++b; 
        d = a++; 
        c++; 
        System.out.println("a = " + a + " b = " + b + " c = " + c + " d = " + d);
	}

}
